content.css:
body {
  font-family: -apple-system, BlinkMacSystemFont, "Segoe UI", Roboto, Helvetica, Arial, sans-serif;
  font-size: 14px;
  line-height: 18px;
}
.container {
  display: flex;
  flex-wrap: wrap;
  justify-content: space-between;
  font-size: 0;
}
.list {
  justify-content: flex-start;
  flex-direction: column;
}
a,
a:visited {
  color: #003569;
  text-decoration: none;
}
a {
  display: inline-block;
  margin-bottom: 8px;
  width: calc(50% - 0.8px);
  text-decoration: none;
  color: black;
  position: relative;
}
@media screen and (min-width: 30em) {
  a {
    width: calc(33.3% - 0.8px);
  }
}
a::before {
  content: "";
  display: block;
  padding-top: 100%;
  /* initial ratio of 1:1*/
}
figure {
  margin: 0;
  overflow: hidden;
  position: absolute;
  top: 0;
  left: 0;
  bottom: 0;
  right: 0;
}
figcaption {
  margin-top: 15px;
  font-size: 12px;
}
img {
  border: none;
  max-width: 100%;
  min-width: 100%;
  height: 100%;
  display: block;
  background: #ccc;
  transition: transform 0.2s ease-in-out;
  object-fit: cover;
}
video {
  border: none;
  max-width: 100%;
  min-width: 100%;
  height: 100%;
  display: block;
  background: #ccc;
  transition: transform 0.2s ease-in-out;
  object-fit: cover;
}
.overlay {
  position: absolute;
  top: 0;
  bottom: 0;
  left: 0;
  right: 0;
  width: 100%;
  justify-content: center;
  -webkit-box-pack: center;
  -webkit-box-align: stretch;
  -webkit-box-orient: vertical;
  -webkit-box-direction: normal;
  align-items: stretch;
  display: flex;
  flex-direction: column;
  background-color: rgba(0, 0, 0, 0.3);
}
.overlay ul {
  list-style: none;
  color: #fff;
  font-weight: 400;
  display: flex;
  flex-direction: row;
  justify-content: space-evenly;
  flex-wrap: wrap;
  -webkit-padding-start: 0;
  -webkit-margin-before: 0;
  -webkit-margin-after: 0;
}
ul.like-comment {
  font-size: 18px;
}
.icon-display li {
  font-size: 16px;
  min-height: 20px;
  background-repeat: no-repeat;
  background-position: left center;
  padding-left: 25px;
  -webkit-margin-before: 1em;
  -webkit-margin-after: 0;
}
.data li {
  line-height: 18px;
  font-size: 14px;
}
