.comment {
  background-size: 20px 20px;
  padding-left: 25px;
}
.like {
  background-size: 20px 20px;
  padding-left: 25px;
}
.sentiment {
  background-size: 20px 20px;
  padding-left: 25px;
}
.hide {
  display: none;
}
.dim {
  opacity: 0.5;
}
.multiple {
  margin-bottom: 50px;
}
.post-item {
  display: flex;
  flex-direction: row;
}
.media-div {
  width: 63%;
  position: relative;
  overflow: hidden;
}
.media-div::after {
  content: "";
  display: block;
  padding-bottom: 100%;
}
.media-div .media {
  position: absolute;
  width: 100%;
  height: 100%;
}
.video-container video {
  width: 100%;
  height: 100%;
}
.video-container {
  width: 100%;
  position: relative;
}
.text-div {
  width: 37%;
  padding: 15px;
  font-size: 14px;
  line-height: 18px;
}
.header {
  border-bottom: 1px solid #efefef;
  margin-bottom: 20px;
  padding-bottom: 15px;
  display: flex;
  flex-direction: row;
}
.author-image {
  height: 40px;
  width: 40px;
  margin-right: 12px;
}
.author-image img {
  width: 100%;
  border-radius: 50%;
}
.author-name {
  padding-top: 10px;
}
.author-name-p {
  padding-right: 8px;
}
.bold {
  font-weight: 600;
}
.data {
  padding-top: 10px;
}
.data li {
  margin-right: 20px;
}
.data p {
  text-align: center;
  -webkit-margin-before: 0px;
  -webkit-margin-after: 0px;
}
.data-label {
  font-size: 11px;
}
.caption {
  max-height: 200px;
  overflow-y: auto;
  overflow-x: hidden;
}
.caption span {
  padding-right: 8px;
}
.summary {
  margin-top: 20px;
}
.summary ul {
  list-style: none;
  font-weight: 400;
  display: flex;
  flex-direction: row;
  justify-content: flex-start;
  flex-wrap: wrap;
  -webkit-padding-start: 0;
  -webkit-margin-before: 0;
  -webkit-margin-after: 12px;
  padding: 0;
}
.like-data,
.comment-data,
.sentiment-data {
  background-size: 16px 16px;
  padding-left: 20px;
  background-repeat: no-repeat;
  margin-right: 20px;
}
.media-type {
  position: absolute;
  top: 10px;
  right: 10px;
  width: 30px;
  height: 30px;
}
.play {
