  position: absolute;
  bottom: 20px;
  color: #fff;
  opacity: 0.5;
  width: 100%;
  z-index: 100;
}
.controls .info {
  position: relative;
  width: 93%;
  left: 20px;
  height: 20px;
  font-size: 18px;
  margin-left: auto;
  margin-right: auto;
  margin-top: 9px;
  padding-left: 30px;
  padding-bottom: 3px;
  vertical-align: middle;
}
.controls .progress-wrapper {
  position: relative;
  width: 95%;
  margin-left: auto;
