content.css:
body {
  font-family: -apple-system, BlinkMacSystemFont, "Segoe UI", Roboto, Helvetica, Arial, sans-serif;
}
.header {
  -webkit-box-orient: horizontal;
  -webkit-box-direction: normal;
  display: flex;
  flex-direction: row;
  flex-shrink: 0;
  align-items: stretch;
  padding: 0;
}
.image-outer {
  flex-basis: 0;
  -webkit-box-flex: 1;
  flex-grow: 1;
  margin-right: 30px;
  flex-shrink: 0;
}
.image-inner {
  display: block;
  margin-left: auto;
  margin-right: auto;
  border-radius: 50%;
  overflow: hidden;
  position: relative;
}
.image {
  height: 100%;
  width: 100%;
  left: 0;
  top: 0;
  position: absolute;
}
section {
  -webkit-box-flex: 2;
  flex-grow: 2;
  flex-shrink: 1;
}
.userName {
  line-height: 40px;
  font-weight: 200;
}
ul.data {
  display: flex;
  flex-direction: row;
  list-style-image: none;
  list-style-position: outside;
  list-style-type: none;
  -webkit-box-direction: normal;
  -webkit-box-orient: horizontal;
  -webkit-margin-after: 20px;
  -webkit-margin-before: 0px;
  -webkit-margin-start: 0px;
  -webkit-margin-end: 0px;
  -webkit-padding-start: 0px;
}
.data li {
  display: list-item;
  margin-right: 40px;
}
.name {
  display: inline;
  font-weight: 600;
}
.web-link {
  display: block;
  white-space: nowrap;
  overflow: hidden;
  text-overflow: ellipsis;
}
.bold {
  font-weight: 600;
}
a,
a:visited {
  color: #003569;
  text-decoration: none;
}
.dim {
  opacity: 0.5;
}
.version {
  text-align: center;
  font-size: 14px;
  color: #657786;
}


